import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Button ,Input} from 'reactstrap';
import { Redirect } from 'react-router';
import validator from 'validator';
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { userSignup } from "../../actions/signupActions";
class Register extends Component {
    constructor(props) {
        super(props);

        this.state = {};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.switchForm = this.switchForm.bind(this);
    }

    handleChange = (e) => {
        e.preventDefault();
        this.setState({ [e.target.name]: e.target.value });
        console.log(this.state);
    }

    nullOrEmpty(str) {
        return str === null || str === "" 
    }
    validateCustRegister = () => {
        let isValid = true;
        if(this.nullOrEmpty(this.state.username)  || this.nullOrEmpty(this.state.email) ||  this.nullOrEmpty(this.state.password)){
        alert("Fields cannot be empty");
           isValid = false;
        }
        else{
            if (!validator.isEmail(this.state.email)) {
                alert('Enter valid Email!')
                isValid = false;
            }

            if(!this.state.username.match(/^[a-zA-Z ]+$/)){
                alert("Name can contain only alphabets")
                isValid = false;
            }
        } 
        return isValid;
     }
     validateOwnerRegister = () => {
        let isValid = true;
        if(this.nullOrEmpty(this.state.username)  || this.nullOrEmpty(this.state.email) ||  this.nullOrEmpty(this.state.password)
            ||this.nullOrEmpty(this.state.zipcode)) {
        alert("Fields cannot be empty");
           isValid = false;
        }
        else{
            if (!validator.isEmail(this.state.email)) {
                alert('Enter valid Email!')
                isValid = false;
            }
            if(!this.state.username.match(/^[a-zA-Z ]+$/)){
                alert("Name can contain only alphabets")
                isValid = false;
            }
            if(!this.state.zipcode.match(/^[0-9]+$/)){
                alert("Zipcode can only contain numbers")
                isValid = false;
            }

        } 
        return isValid;
     }
    handleSubmit = (e) => {
        e.preventDefault();

        const buyerData = {
            name: this.state.username,
            email: this.state.email,
            password: this.state.password,
            //restaurantname: "N/A",
            zipcode: "N/A",
            owner: false
        }

        const ownerData = {
            name: this.state.username,
            email: this.state.email,
            password: this.state.password,
            //restaurantname: this.state.restaurantname,
            zipcode: this.state.zipcode,
            owner: true
        }
        console.log(ownerData);
        if (!this.state.owner) {

                this.props.userSignup(buyerData);

        } else {


                this.props.userSignup(ownerData);


        }

    }

    //switch between user and owner sign up form
    switchForm = (e) => {
        (!this.state.owner) ? this.setState({ owner: true }) : this.setState({ owner: false });
    }

    render() {

        //localStorage.setItem("message",this.props.user.message)
        let redirectHome = null;
        var ownerForm = null;
        var userForm = null;
        var accountType = "Owner";
        if(this.props.user.message === "User Registration successful"){
            redirectHome = <Redirect to="/Login" />
            this.props.user.message = null;
        }
        else{
            redirectHome = <Redirect to="/Register" />

        }

        // if(this.state.message == "User email already registered"){
        //     alert("Email already exists");
        // }
        if (this.state.owner) {
            ownerForm =
            <div className="form-group">
                <div >
                    Restaurant Name:  <input type="text" class="form-control" name="name" onChange={this.handleChange} placeholder="Enter Restaurant Name" pattern="^[A-Za-z0-9 ]+$" required />
                    ZipCode: <input type="text" class="form-control" name="zipcode" onChange={this.handleChange} placeholder="Enter zip code" pattern="^[0-9]{5,6}$" required />

                </div>
                </div>
            accountType = "User";
        } else{
            userForm =
            <div >
            Name: <input type="text" class="form-control" name="name" onChange={this.handleChange} placeholder="Enter Customer Name" pattern="^[A-Za-z0-9 ]+$" required />
        </div>
         accountType = "Owner";
        }

        return (


            <div className ="body-login">
                {redirectHome}

            <div className="container">
              <div className="login-form">
                <div className="main-div">
                  <div className="panel">
                  <h1>Let's get started.</h1>
                    <p>Please enter required details to register.</p>
                  </div>
                  {userForm}
                  {ownerForm}
                  <form onSubmit={this.handleSubmit}>
                  <div className="form-group">

                  Email:  <input type="text" class="form-control" name="email" onChange={this.handleChange} placeholder="Email Id" pattern="^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$'%&*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])$" required />

                  </div>
                  <div className="form-group">

                  Password:  <input type="password" class="form-control" name="password" onChange={this.handleChange} placeholder="Password" required />

                  </div>
                  <div className="form-group">
                  <Button className="btn btn-primary" >Register</Button> &nbsp;
                <Button onClick={this.switchForm}>Sign Up as {accountType}</Button>
                  </div>
                  <h4>Already have an account? <Link to="/login">Login</Link></h4><br />
                  <div  style={{color: "#ff0000"}}><h4>{this.props.user.message}</h4></div> 
                  </form>
                  </div>
                </div>
              </div>
            </div>
        )
    }
}
Register.propTypes = {
    userSignup: PropTypes.func.isRequired,
    user: PropTypes.object.isRequired,

  };

  const mapStateToProps = (state) => {
    return {
      user: state.signup.user,
    };
  };

  export default connect(mapStateToProps, { userSignup})(Register);
