        this.setState({ [e.target.name]: e.target.value });
        }
        goback = (e) =>{
            e.preventDefault();
            const {history} = this.props;
            history.push('/customerhome'); 
          }

          saveFile = (e) => {
            e.preventDefault();
            this.setState({file:e.target.files[0]});
            this.setState({fileName:e.target.files[0].name});

