import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import AddToCart from '../Dashboard/AddToCart';
import {FaShoppingCart} from 'react-icons/fa';

import {

  Button,Offcanvas

} from 'react-bootstrap';
//create the Navbar Component
class Navbar extends Component {
  constructor(props) {
    super(props);
   this.state = {
      openModal: false,
      openCanvas:false
    };
  }
viewCart = (e) => {
    e.preventDefault();
    this.setState({ openModal: true });
  }
   closeModal = () => {
     this.setState({ openModal: false });
    //  const {history} = this.props;
    //  history.push('/checkout');
  }
  handleShow = (e)=>{
    e.preventDefault();
    this.setState({ openCanvas: true });
  }
  handleClose= () => {
    this.setState({
      sideshow:false
    });
 }
  //handle logout to destroy the cookie
  // handleLogout = () => {
  //   cookie.remove("cookie", { path: "/" });
  // };
  checkOut = () => {
    this.setState({ openModal: false });
    const { history } = this.props;
    history.push('/checkout');
  }
  logout = e => {
        e.preventDefault();
        localStorage.setItem("userid","");
        window.localStorage.clear();
        const {history} = this.props;
        history.push('/login'); 
      }
  render() {
    const {openModal} = this.state
    //if Cookie is set render Logout Button

    const userNameSessionVal = localStorage.getItem('userid');
    let sessionAvail = null;
    if (userNameSessionVal != null && userNameSessionVal !== undefined && userNameSessionVal !== "") {
      sessionAvail = (
        <div className = "cartitems">
          {/* <AddToCart /> */}
           <Button className="cartleft" onClick={this.viewCart}><FaShoppingCart/>Cart</Button>
           <Button className="btn-logout" onClick={this.logout}>Logout</Button>
           <Button className="btn-offcanvas-show" onClick={this.handleShow} >
              Launch
            </Button>
        </div>
      );
    }
    let redirectVar = null;
    // if (!cookie.load("cookie")) {
    //   redirectVar = <Redirect to="/login" />;
    // }
    return (
      <div  >
        {redirectVar}

        <nav className="navbar navbar-inverse">
          <div className="container-fluid">
            <div className="navbar-header">

              <div className=" navbar-brand "><h1 className="ubereats"><span style={{ color:"white"}}>Uber</span> <span style={{ color:"green" }}>Eats</span></h1></div>
            {sessionAvail}
                { openModal
              ? (
                <AddToCart
                  closeModal={this.closeModal}
                  viewCart={this.viewCart}
                  handleSubmit={this.checkOut}
                />
              )
              : null}

              { openCanvas
                ?(
                  <Offcanvas className="offcanvas" show={this.state.sideshow} >
                  <Offcanvas.Header closeButton onHide={this.handleClose}>
                    <Offcanvas.Title></Offcanvas.Title>
                  </Offcanvas.Header>
                  <Offcanvas.Body>
                  <div className='form-buttons-offcanvas'>

                    <Button className="btn-offcanvas" onClick={this.profile}>Profile</Button>

                    <Button className="btn-offcanvas" onClick={this.findfood}>Find Food</Button>

                    <Button className="btn-offcanvas" onClick={this.orders}>Orders</Button>

                    <Button className="btn-offcanvas" onClick={this.showfavourites}>Favourites</Button>


                    </div> 
                  </Offcanvas.Body>
                </Offcanvas>
                )
              :null}
            </div>
          </div>
        </nav>
      </div>
    );
  }
}

export default Navbar;



