import React, { Component } from "react";
import { Link } from "react-router-dom";
import cookie from "react-cookies";
import { Redirect } from "react-router";
import AddToCart from '../Dashboard/AddToCart';
import {FaShoppingCart} from 'react-icons/fa';
import {MdViewDay} from 'react-icons/md';
import { Button,NavDropdown,Container,Navbar,Nav} from "react-bootstrap";
import LaunchCustomer from "../Dashboard/LaunchCustomer";
//create the Navbar Component
class Navbar1 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      openModal: false,
      openCanvas:false
    };
  }
viewCart = (e) => {
    e.preventDefault();
    this.setState({ openModal: true });
  }
   closeModal = () => {
     this.setState({ openModal: false });

  }
  closeCanvas = () =>{
    this.setState({ openCanvas: false });
  }
  viewCustomerLaunch = () =>{
    this.setState({ openCanvas: true });
  }

  checkOut = () => {
    this.setState({ openModal: false });
    const { history } = this.props;
    history.push('/checkout');
  }
  logout = e => {
        e.preventDefault();
        localStorage.setItem("userid","");
        window.localStorage.clear();
        const {history} = this.props;
        history.push('/login'); 
      }
      profile = e => {
        e.preventDefault();
        const {history} = this.props;
        history.push('/customerprofile'); 
        this.setState({ openCanvas: false });
      }
      showfavourites = e => {
        e.preventDefault();
        const {history} = this.props;
        history.push('/favourites'); 
        this.setState({ openCanvas: false });
      }
      findfood = e => {
        e.preventDefault();
        const {history} = this.props;
        history.push('/restdashboard'); 
        this.setState({ openCanvas: false });
      }
      orders = e => {
        e.preventDefault();
        const {history} = this.props;
        history.push('/customerorder'); 
        this.setState({ openCanvas: false });
      }
      home = e => {
        e.preventDefault();
        const {history} = this.props;
        history.push('/customerhome'); 
        this.setState({ openCanvas: false });
      }
  render() {
    const {openModal} = this.state
    const {openCanvas} = this.state
    //if Cookie is set render Logout Button

    const userNameSessionVal = localStorage.getItem('userid');
    let sessionAvail = null;
    if (userNameSessionVal != null && userNameSessionVal !== undefined && userNameSessionVal !== "") {
      sessionAvail = (
        <div className = "cartitems  ">
          {/* <AddToCart /> */}
           <Button className="cartleft " onClick={this.viewCart}><FaShoppingCart/>Cart</Button>
           <Button className="btn-logout " onClick={this.logout}>Logout</Button>
            <Button className="btn-offcanvas-show " onClick={this.viewCustomerLaunch} >
            <MdViewDay/>
            </Button>
        </div>
      );
    }
    let redirectVar = null;
    // if (!cookie.load("cookie")) {
    //   redirectVar = <Redirect to="/login" />;
    // }
    return (
      <div  >
        {redirectVar}


        <Navbar bg="white" expand="lg">
          <Container>
            <Navbar.Brand ><h1 className="ubereats"><span style={{ color:"white"}}>Uber</span> <span style={{ color:"green" }}>Eats</span></h1></Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto bg-white">
              {sessionAvail}
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>

        {/* <nav className="navbar navbar-inverse">
          <div className="container-fluid">
            <div className="navbar-header">

              <div className=" navbar-brand "><h1 className="ubereats"><span style={{ color:"white"}}>Uber</span> <span style={{ color:"green" }}>Eats</span></h1></div>
            {sessionAvail}
                { openModal
              ? (
                <AddToCart
                  closeModal={this.closeModal}
                  viewCart={this.viewCart}
                  handleSubmit={this.checkOut}

                />
              )
              : null}
              { openCanvas
              ? (
                <LaunchCustomer
                  closeCanvas={this.closeCanvas}
                  viewCustomerLaunch={this.viewCustomerLaunch}   
                  profile ={this.profile}
                  orders={this.orders} 
                  showfavourites={this.showfavourites}
                  findfood={this.findfood}
                  home={this.home}
                />
              )
              : null}
            </div>
          </div>
        </nav>*/}
      </div> 
    );
  }
}

export default Navbar1;
