          e.preventDefault();
          this.setState({file:e.target.files[0]});
          this.setState({fileName:e.target.files[0].name});

        };
        handleModalClose(){
          this.setState({show:!this.state.show}) 
          const {history} = this.props;
          history.push('/restaurantprofile'); 
      }

    uploadFile = (e) => {
          e.preventDefault();
          const formData = new FormData();
