import {

  Button,

} from 'react-bootstrap';
import LaunchCustomer from "../Dashboard/LaunchCustomer";
//create the Navbar Component
class Navbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      openModal: false,
      openCanvas:false
    };
  }
viewCart = (e) => {
    e.preventDefault();
    this.setState({ openModal: true });
  }
   closeModal = () => {
     this.setState({ openModal: false });

